.searchBar {
    border: 1px solid grey;
    border-radius: 20px;
    height: 30px;
    width: 100%;
    padding: 2px 23px 2px 30px;
    outline: 0;
    background-color: #f5f5f5;
    text-align: center;
    box-sizing: border-box;
}

