    width: 100%;
    margin-bottom: 20px;
    padding-bottom: 5px;
    margin: auto;
    position: relative;
}

.search-icon {
    top: 6px;
    left: 8px;
    width: 14px;
    position: absolute;
}


.searchBar:hover,
.searchBar:focus {
    background-color: white;
